mapper.update:
UPDATE FEED SET
TITLE = #{title},
CONTENTS = #{contents},
TRIPDATE = #{tripDate},
PUBLICS = #{publics}
WHERE FEEDNUM = #{feedNum}

UPDATE FEED
SET HIT = HIT + 1
WHERE FEEDNUM = #{feedNum}

mapper.select:
SELECT COUNT(*)
  FROM LIKES
 WHERE FEEDNUM = #{feedNum}
   AND ID = #{id}

SELECT COUNT(*)
  FROM LIKES
 WHERE FEEDNUM = #{feedNum}
